mapper.select:
select
,
from ts_interface_basic_info where description like concat(concat('%', #{description,jdbcType=VARCHAR}),'%')

mapper.insert:
insert into ts_interface_basic_info (id, group_id,description, path,
  method, create_time, create_by, 
  update_time, update_by, body
  )
values (#{id,jdbcType=VARCHAR},#{groupId,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR}, #{path,jdbcType=VARCHAR},
  #{method,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{createBy,jdbcType=VARCHAR}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}, #{body,jdbcType=LONGVARCHAR}
  )
